const crypto = require('crypto');

// Get these from your Service Account JSON key file
const serviceAccountEmail = 'your_service_account_email_here.iam.gserviceaccount.com';

// Paste your private key from the JSON file (the whole thing including BEGIN/END)
const privateKey = `Your_privateKey_here`;

const response = $input.first().json;
const bucket = response.bucket;
const objectName = response.name;

// URL expires in 7 days (604800 seconds)
const expirationSeconds = 315360000;
const expiration = Math.floor(Date.now() / 1000) + expirationSeconds;

// Create signature
const stringToSign = `GET\n\n\n${expiration}\n/${bucket}/${objectName}`;
const sign = crypto.createSign('RSA-SHA256');
sign.update(stringToSign);
const signature = encodeURIComponent(sign.sign(privateKey, 'base64'));

const signedUrl = `https://storage.googleapis.com/${bucket}/${objectName}?GoogleAccessId=${serviceAccountEmail}&Expires=${expiration}&Signature=${signature}`;

return [{ 
  json: { 
    imageUrl: signedUrl,
    expiresAt: new Date(expiration * 1000).toISOString()
  } 
}];
